#
# Validate 'mdat' object generated by mmdata()
#
.validate.mdat <- function(mdat) {
  # Need to validate only once
  if (class(mdat) == "mdat" && attr(mdat, "validated")) {
    return(mdat)
  }

  # Check mdat
  if (class(mdat) != "mdat") {
    stop("'mdat' S3 object created by mmdata() expected")
  }

  # Check attributes
  if (length(mdat) != length(attr(mdat, "model_names"))) {
    stop("Invalid 'model_names'")
  }

  if (length(mdat) != length(attr(mdat, "setids"))) {
    stop("Invalid 'setids'")
  }

  attr(mdat, "validated") <- TRUE
  mdat
}

#
# Validate 'fmdat' object generated by reformat_data()
#
.validate.fmdat <- function(fmdat) {
  # Need to validate only once
  if (class(fmdat) == "fmdat" && attr(fmdat, "validated")) {
    return(fmdat)
  }

  # Validate class items and attributes
  item_names <- c("labels", "ranks", "rank_idx")
  attr_names <- c("model_name", "setid", "nn", "np", "args", "validated")
  arg_names <- c("na.last", "ties.method", "levels", "model_name", "setid")
  .validate_basic(fmdat, "fmdat", "reformat_data", item_names, attr_names,
                  arg_names)

  # Check values of class items
  if (length(fmdat[["labels"]]) == 0
      || length(fmdat[["labels"]]) != length(fmdat[["ranks"]])
      || length(fmdat[["labels"]]) != length(fmdat[["rank_idx"]])) {
    stop("Items in 'fmdat' must be of the same length")
  }

  # Labels
  assertthat::assert_that(is.ordered(fmdat[["labels"]]),
                          length(levels(fmdat[["labels"]])) == 2)

  # Ranks
  assertthat::assert_that(is.atomic(fmdat[["ranks"]]),
                          is.vector(fmdat[["ranks"]]),
                          is.numeric(fmdat[["ranks"]]))

  # Rank index
  assertthat::assert_that(is.atomic(fmdat[["rank_idx"]]),
                          is.vector(fmdat[["rank_idx"]]),
                          is.integer(fmdat[["rank_idx"]]))

  attr(fmdat, "validated") <- TRUE
  fmdat
}

#
# Validate 'cmats' object generated by create_confmats()
#
.validate.cmats <- function(cmats) {
  # Need to validate only once
  if (class(cmats) == "cmats" && attr(cmats, "validated")) {
    return(cmats)
  }

  # Validate class items and attributes
  item_names <- c("pos_num", "neg_num", "tp", "fp", "tn", "fn", "ranks")
  attr_names <- c("model_name", "setid", "nn", "np", "args", "cpp_errmsg",
                  "src", "validated")
  arg_names <- c("na.last", "ties.method", "levels", "model_name", "setid",
                 "keep_fmdat")
  .validate_basic(cmats, "cmats", "create_confmats", item_names, attr_names,
                  arg_names)

  # Check values of class items
  n <- length(cmats[["tp"]])
  if (length(cmats[["fp"]]) != n || length(cmats[["tn"]]) != n
      || length(cmats[["fn"]]) != n) {
    stop("tp, fp, tn, and fn in 'cmats' must be the same length")
  }

  # TP
  assertthat::assert_that(is.atomic(cmats[["tp"]]),
                          is.vector(cmats[["tp"]]),
                          is.numeric(cmats[["tp"]]),
                          cmats[["tp"]][1] == 0,
                          cmats[["tp"]][n] == cmats[["pos_num"]])

  # FP
  assertthat::assert_that(is.atomic(cmats[["fp"]]),
                          is.vector(cmats[["fp"]]),
                          is.numeric(cmats[["fp"]]),
                          cmats[["fp"]][1] == 0,
                          cmats[["fp"]][n] == cmats[["neg_num"]])

  # FN
  assertthat::assert_that(is.atomic(cmats[["fn"]]),
                          is.vector(cmats[["fn"]]),
                          is.numeric(cmats[["fn"]]),
                          cmats[["fn"]][1] == cmats[["pos_num"]],
                          cmats[["fn"]][n] == 0)

  # TN
  assertthat::assert_that(is.atomic(cmats[["tn"]]),
                          is.vector(cmats[["tn"]]),
                          is.numeric(cmats[["tn"]]),
                          cmats[["tn"]][1] == cmats[["neg_num"]],
                          cmats[["tn"]][n] == 0)

  attr(cmats, "validated") <- TRUE
  cmats
}

#
# Validate 'pevals' object generated by calc_measures()
#
.validate.pevals <- function(pevals) {
  # Need to validate only once
  if (class(pevals) == "pevals" && attr(pevals, "validated")) {
    return(pevals)
  }

  # Validate class items and attributes
  item_names <- c("pos_num", "neg_num", "error", "accuracy", "specificity",
                  "sensitivity", "precision")
  attr_names <- c("model_name", "setid", "nn", "np", "args", "cpp_errmsg",
                  "src", "validated")
  arg_names <- c("na.last", "ties.method", "levels", "model_name", "setid",
                 "keep_fmdat")
  .validate_basic(pevals, "pevals", "calc_measures", item_names, attr_names,
                  arg_names)

  # Check values of class items
  n <- length(pevals[["error"]])
  if (length(pevals[["accuracy"]]) != n || length(pevals[["specificity"]]) != n
      || length(pevals[["sensitivity"]]) != n
      || length(pevals[["precision"]]) != n) {
    stop("All evaluation vectors must be the same length")
  }

  # Error rate
  assertthat::assert_that(is.atomic(pevals[["error"]]),
            is.vector(pevals[["error"]]),
            is.numeric(pevals[["error"]]))

  # Accuracy
  assertthat::assert_that(is.atomic(pevals[["accuracy"]]),
            is.vector(pevals[["accuracy"]]),
            is.numeric(pevals[["accuracy"]]))

  # Error rate & Arruracy
  assertthat::assert_that(pevals[["error"]][1] + pevals[["accuracy"]][1] == 1,
                          pevals[["error"]][n] + pevals[["accuracy"]][n] ==1)

  # SP
  assertthat::assert_that(is.atomic(pevals[["specificity"]]),
                          is.vector(pevals[["specificity"]]),
                          is.numeric(pevals[["specificity"]]),
                          pevals[["specificity"]][1] == 1,
                          pevals[["specificity"]][n] == 0)

  # SN
  assertthat::assert_that(is.atomic(pevals[["sensitivity"]]),
                          is.vector(pevals[["sensitivity"]]),
                          is.numeric(pevals[["sensitivity"]]),
                          pevals[["sensitivity"]][1] == 0,
                          pevals[["sensitivity"]][n] == 1)

  # PREC
  assertthat::assert_that(is.atomic(pevals[["precision"]]),
                          is.vector(pevals[["precision"]]),
                          is.numeric(pevals[["precision"]]),
                          pevals[["precision"]][1] == pevals[["precision"]][2])

  attr(pevals, "validated") <- TRUE
  pevals
}

#
# Validate 'roc_curve' object generated by create_roc()
#
.validate.roc_curve <- function(roc_curve) {
  # Need to validate only once
  if (class(roc_curve) == "roc_curve" && attr(roc_curve, "validated")) {
    return(roc_curve)
  }

  # Validate class items and attributes
  .validate_curve(roc_curve, "roc_curve", "create_roc")

  attr(roc_curve, "validated") <- TRUE
