#
# Validate 'roc_curve' or 'prc_curve'
#
.validate_curve <- function(obj, class_name, func_name) {
  # Validate class items and attributes
  item_names <- c("x", "y", "orig_points")
  attr_names <- c("model_name", "setid", "nn", "np", "auc", "partial", "pauc",
                  "x_limits", "y_limits", "args", "cpp_errmsg1", "cpp_errmsg2",
                  "src", "validated")
  arg_names <- c("x_interval", "na.last", "ties.method", "levels",
                 "model_name", "setid", "keep_fmdat", "keep_cmats")
  .validate_basic(obj, class_name, func_name, item_names, attr_names,
                  arg_names)

  # Check values of class items
  if ((length(obj[["x"]]) != length(obj[["y"]]))
      || (length(obj[["x"]]) != length(obj[["orig_points"]]))) {
    stop("x, y, and orig_points must be the same length")
  } else if (!(length(obj[["x"]]) > 2)) {
    stop("x, y, and orig_points must be >2")
  }

  # Check values of class attributes
  # AUC
  assertthat::assert_that((attr(obj, "auc") >= 0) && (attr(obj, "auc") <= 1))

  # Partial AUC
  assertthat::assert_that(is.atomic(attr(obj, "partial")),
                          is.logical(attr(obj, "partial")),
                          (is.na(attr(obj, "pauc"))
                           || (attr(obj, "pauc") >= 0
                               && attr(obj, "pauc") <= 1)))

  # X limits
  assertthat::assert_that(is.atomic(attr(obj, "x_limits")),
                          is.vector(attr(obj, "x_limits")),
                          is.numeric(attr(obj, "x_limits")),
                          length(attr(obj, "x_limits")) == 2,
                          (attr(obj, "x_limits")[1] >= 0
                           && attr(obj, "x_limits")[1] <= 1),
                          (attr(obj, "x_limits")[2] >= 0
                           && attr(obj, "x_limits")[2] <= 1),
                          attr(obj, "x_limits")[1] < attr(obj, "x_limits")[2])

  # Y limits
  assertthat::assert_that(is.atomic(attr(obj, "y_limits")),
                          is.vector(attr(obj, "y_limits")),
                          is.numeric(attr(obj, "y_limits")),
                          length(attr(obj, "y_limits")) == 2,
                          (attr(obj, "y_limits")[1] >= 0
                           && attr(obj, "y_limits")[1] <= 1),
                          (attr(obj, "y_limits")[2] >= 0
                           && attr(obj, "y_limits")[2] <= 1),
                          attr(obj, "y_limits")[1] < attr(obj, "y_limits")[2])
}

#
# Validate 'curves' object generated by create_curves()
#
.validate.curves <- function(curves) {
  # Need to validate only once
  if (class(curves) == "curves" && attr(curves, "validated")) {
    return(curves)
  }

  # Validate class items and attributes
  item_names <- c("roc", "prc")
  attr_names <- c("model_name", "setid", "nn", "np", "args", "src",
                  "validated")
  arg_names <- c("x_interval", "na.last", "ties.method", "model_name", "setid",
                 "keep_fmdat", "keep_cmats")
  .validate_basic(curves, "curves", "calc_measures", item_names, attr_names,
                  arg_names)

  # Check values of class items
  curves[["roc"]] <- .validate(curves[["roc"]])
  curves[["prc"]] <- .validate(curves[["prc"]])

  attr(curves, "validated") <- TRUE
  curves
}

