#' Plot ROC and Precision-Recall curves for multiple models with multiple datasets
#'
#' The \code{plot} function plots ROC and Precion-Recall curves.
#'
#' @param x \code{smcurves} object generated by
#'   \code{\link{evalmods_m}}.
#'
#' @param curvetype a character vector with the following curve types.
#'   \itemize{
#'     \item "ROC"
#'     \item "PRC"
#'     \item c("ROC", "PRC")
#'   }
#'
#' @param show_legend A boolean value to indicate whether the legend is added.
#'
#' @param ... Not used by this method.
#'
#' @return The \code{plot} function shows a plot and returns NULL.
#'
#' @seealso \code{\link{evalmods}} for generating \code{smcurves}.
#'   \code{\link{autoplot.smcurves}} for plotting curves with
#'   \code{\link[ggplot2]{ggplot2}}.
#'
#' @examples
#'
#' ## Prepare input data
#' samps <- create_sim_samples(10, 100, 100, "all")
#' mdat <- mmdata(samps[["scores"]], samps[["labels"]],
#'                modnames = samps[["modnames"]],
#'                setids = samps[["setids"]])
#'
#' ## Generate an mscurve object
#' curves <- evalmods_m(mdat)
#'
#' ## Plot both ROC and Precision-Recall curves
#' plot(curves)
#'
#' ## Plot a ROC curve
#' plot(curves, curvetype = "ROC")
#'
#' ## Plot a Precision-Recall curve
#' plot(curves, curvetype = "PRC")
#'
#' @export
plot.mmcurves <- function(x, curvetype = c("ROC", "PRC"), show_ci = FALSE,
                          show_legend = TRUE, ...) {

  # === Validate input arguments ===
  .check_show_legend(show_legend)
  .validate(x)
  .check_curvetype(curvetype)

  # === Create a plot ===
  show_legend2 <- show_legend
  if ("ROC" %in% curvetype && "PRC" %in% curvetype) {
    if (show_legend) {
      m <- matrix(c(1, 2, 3, 3), nrow = 2, ncol = 2, byrow = TRUE)
      layout(mat = m, heights = c(0.85, 0.15))
    } else {
      m <- matrix(c(1, 2), nrow = 1, ncol = 2)
      layout(mat = m)
    }
    on.exit(layout(1), add = TRUE)
    show_legend2 <- FALSE
  }

  if ("ROC" %in% curvetype) {
    plot(x[["rocs"]], show_ci, show_legend2, ...)
  }

  if ("PRC" %in% curvetype) {
    plot(x[["prcs"]], show_ci, show_legend2, ...)
  }

  if ("ROC" %in% curvetype && "PRC" %in% curvetype) {
    .show_legend(x, show_legend)
  }
}

#
# Plot ROC curves
#
plot.mmroc <- function(x, show_ci = FALSE, show_legend = TRUE, ...) {
  old_pty <- par(pty = "s")
  on.exit(par(old_pty), add = TRUE)

  if (show_legend) {
    m <- matrix(c(1, 2), nrow = 2, ncol = 1)
    layout(mat = m, heights = c(0.85, 0.15))
    on.exit(layout(1), add = TRUE)
  }

  # === Create a plot ===
  .plot_avg(x, "ROC", "1 - Specificity", "Sensitivity", show_ci)
  abline(a = 0, b = 1, col = "grey", lty = 3)

  .show_legend(x, show_legend)
}

#
# Plot Precision-Recall curves
#
plot.mmprc <- function(x, show_ci = FALSE, show_legend = TRUE, ...) {
  old_pty <- par(pty = "s")
  on.exit(par(old_pty), add = TRUE)

  if (show_legend) {
    m <- matrix(c(1, 2), nrow = 2, ncol = 1)
    layout(mat = m, heights = c(0.85, 0.15))
    on.exit(layout(1), add = TRUE)
  }

  # === Create a plot ===
  np <- attr(x[[1]], "np")
  nn <- attr(x[[1]], "nn")

  .plot_avg(x, "Precision-Recall", "Recall", "Precision", show_ci)
  abline(h = np / (np + nn), col = "grey", lty = 3)

  .show_legend(x, show_legend)
}

