#
# Validate curves object generated by pl_main()
#
.validate_curves_common <- function(curves, class_name) {
  # Need to validate only once
  if (is(curves, class_name) && attr(curves, "validated")) {
    return(curves)
  }

  # Check curves
  if (!is(curves, class_name)) {
    stop(paste0(class_name, " object created by pl_main() expected"))
  }

  attr(curves, "validated") <- TRUE
  curves
}

#
# Validate 'sscurves' object generated by pl_main()
#
.validate.sscurves <- function(sscurves) {
  .validate_curves_common(sscurves, "sscurves")
}

#
# Validate 'ssroc' object generated by pl_main()
#
.validate.ssroc <- function(ssroc) {
  .validate_curves_common(ssroc, "ssroc")
}

